  return(
    <div className="hero-container container-fluid">
      <img src={WineMeLogo} alt="Wine Me Logo" className="WineMeLogo"/>
      <h3>Drink Wine, Rate Wine</h3>
      {IsLoading? 
        <div className="spinner">
          {console.log("Loading")}
